 import React, { useState, useContext } from 'react';
import expenseService from '../../services/expenseService';
import { AuthContext } from '../../context/AuthContext';
import axios from 'axios';

const ExpenseSubmission = () => {
  const { user } = useContext(AuthContext);
  const [formData, setFormData] = useState({
    amount: '',
    currency: user?.company?.currency || 'USD',
    category: 'Food',
    description: '',
    date: '',
  });
  const [receipt, setReceipt] = useState(null);
  const [message, setMessage] = useState('');
  const [ocrProcessing, setOcrProcessing] = useState(false);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleFileChange = (e) => {
    setReceipt(e.target.files[0]);
  };

  const handleOCR = async () => {
    if (!receipt) {
      setMessage('Please select a receipt image first');
      return;
    }

    setOcrProcessing(true);
    const ocrFormData = new FormData();
    ocrFormData.append('receipt', receipt);

    try {
      const token = localStorage.getItem('token');
      const response = await axios.post(
        `${process.env.REACT_APP_API_URL || 'http://localhost:5000/api'}/ocr/process`,
        ocrFormData,
        {
          headers: {
            Authorization: `Bearer ${token}`,
            'Content-Type': 'multipart/form-data',
          },
        }
      );

      setFormData({
        ...formData,
        amount: response.data.amount,
        description: response.data.description,
        date: response.data.date,
      });
      setMessage('Receipt processed successfully!');
    } catch (error) {
      setMessage('OCR processing failed');
    } finally {
      setOcrProcessing(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const submitFormData = new FormData();
    Object.keys(formData).forEach((key) => {
      submitFormData.append(key, formData[key]);
    });
    if (receipt) {
      submitFormData.append('receipt', receipt);
    }

    try {
      await expenseService.submitExpense(submitFormData);
      setMessage('Expense submitted successfully!');
      setFormData({
        amount: '',
        currency: user?.company?.currency || 'USD',
        category: 'Food',
        description: '',
        date: '',
      });
      setReceipt(null);
    } catch (error) {
      setMessage('Failed to submit expense');
    }
  };

  return (
    <div className="expense-submission">
      <h2>Submit Expense</h2>
      {message && <div className="message">{message}</div>}
      <form onSubmit={handleSubmit}>
        <input
          type="number"
          name="amount"
          placeholder="Amount"
          value={formData.amount}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="currency"
          placeholder="Currency"
          value={formData.currency}
          onChange={handleChange}
          required
        />
        <select name="category" value={formData.category} onChange={handleChange} required>
          <option value="Travel">Travel</option>
          <option value="Food">Food</option>
          <option value="Accommodation">Accommodation</option>
          <option value="Office Supplies">Office Supplies</option>
          <option value="Entertainment">Entertainment</option>
          <option value="Other">Other</option>
        </select>
        <textarea
          name="description"
          placeholder="Description"
          value={formData.description}
          onChange={handleChange}
          required
        />
        <input type="date" name="date" value={formData.date} onChange={handleChange} required />
        <input type="file" onChange={handleFileChange} accept="image/*,application/pdf" />
        {receipt && (
          <button type="button" onClick={handleOCR} disabled={ocrProcessing}>
            {ocrProcessing ? 'Processing...' : 'Process Receipt with OCR'}
          </button>
        )}
        <button type="submit">Submit Expense</button>
      </form>
    </div>
  );
};

export default ExpenseSubmission;
