import React, { useState, useContext } from 'react';
import expenseService from '../../services/expenseService';
import { AuthContext } from '../../context/AuthContext';
import axios from 'axios';

const ExpenseSubmission = () => {
  const { user } = useContext(AuthContext);
  const [formData, setFormData] = useState({
    amount: '',
    currency: user?.company?.currency || 'USD',
    category: 'Food',
    description: '',
    date: new Date().toISOString().split('T')[0],
  });
  const [receipt, setReceipt] = useState(null);
  const [previewUrl, setPreviewUrl] = useState(null);
  const [message, setMessage] = useState('');
  const [error, setError] = useState('');
  const [ocrProcessing, setOcrProcessing] = useState(false);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setReceipt(file);
    
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setPreviewUrl(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleOCR = async () => {
    if (!receipt) {
      setError('Please select a receipt image first');
      setTimeout(() => setError(''), 3000);
      return;
    }

    setOcrProcessing(true);
    const ocrFormData = new FormData();
    ocrFormData.append('receipt', receipt);

    try {
      const token = localStorage.getItem('token');
      const response = await axios.post(
        `${process.env.REACT_APP_API_URL || 'http://localhost:5000/api'}/ocr/process`,
        ocrFormData,
        {
          headers: {
            Authorization: `Bearer ${token}`,
            'Content-Type': 'multipart/form-data',
          },
        }
      );

      setFormData({
        ...formData,
        amount: response.data.amount,
        description: response.data.description,
        date: response.data.date,
      });
      setMessage('Receipt processed successfully!');
      setTimeout(() => setMessage(''), 3000);
    } catch (error) {
      setError('OCR processing failed');
      setTimeout(() => setError(''), 3000);
    } finally {
      setOcrProcessing(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const submitFormData = new FormData();
    Object.keys(formData).forEach((key) => {
      submitFormData.append(key, formData[key]);
    });
    if (receipt) {
      submitFormData.append('receipt', receipt);
    }

    try {
      await expenseService.submitExpense(submitFormData);
      setMessage('Expense submitted successfully! üéâ');
      setFormData({
        amount: '',
        currency: user?.company?.currency || 'USD',
        category: 'Food',
        description: '',
        date: new Date().toISOString().split('T')[0],
      });
      setReceipt(null);
      setPreviewUrl(null);
      setTimeout(() => setMessage(''), 3000);
    } catch (error) {
      setError('Failed to submit expense');
      setTimeout(() => setError(''), 3000);
    }
  };

  const getCategoryIcon = (category) => {
    const icons = {
      Travel: '‚úàÔ∏è',
      Food: 'üçî',
      Accommodation: 'üè®',
      'Office Supplies': 'üìé',
      Entertainment: 'üé¨',
      Other: 'üìã',
    };
    return icons[category] || 'üìã';
  };

  return (
    <div className="expense-submission-container">
      <div className="submission-header">
        <h2>üí∞ Submit New Expense</h2>
        <p className="subtitle">Fill in the details or upload a receipt for automatic extraction</p>
      </div>

      {message && <div className="message">{message}</div>}
      {error && <div className="error">{error}</div>}

      <div className="submission-layout">
        <div className="form-section">
          <form onSubmit={handleSubmit} className="expense-form-modern">
            <div className="form-group">
              <label>
                Amount <span className="required">*</span>
              </label>
              <div className="input-with-icon">
                <span className="input-icon">üíµ</span>
                <input
                  type="number"
                  name="amount"
                  placeholder="0.00"
                  step="0.01"
                  value={formData.amount}
                  onChange={handleChange}
                  required
                />
              </div>
            </div>

            <div className="form-row">
              <div className="form-group">
                <label>
                  Currency <span className="required">*</span>
                </label>
                <input
                  type="text"
                  name="currency"
                  placeholder="USD"
                  value={formData.currency}
                  onChange={handleChange}
                  required
                />
              </div>

              <div className="form-group">
                <label>
                  Date <span className="required">*</span>
                </label>
                <input
                  type="date"
                  name="date"
                  value={formData.date}
                  onChange={handleChange}
                  required
                />
              </div>
            </div>

            <div className="form-group">
              <label>
                Category <span className="required">*</span>
              </label>
              <div className="category-grid">
                {['Travel', 'Food', 'Accommodation', 'Office Supplies', 'Entertainment', 'Other'].map(
                  (cat) => (
                    <label key={cat} className="category-option">
                      <input
                        type="radio"
                        name="category"
                        value={cat}
                        checked={formData.category === cat}
                        onChange={handleChange}
                      />
                      <span className="category-card">
                        <span className="category-icon">{getCategoryIcon(cat)}</span>
                        <span className="category-name">{cat}</span>
                      </span>
                    </label>
                  )
                )}
              </div>
            </div>

            <div className="form-group">
              <label>
                Description <span className="required">*</span>
              </label>
              <textarea
                name="description"
                placeholder="Enter expense description..."
                value={formData.description}
                onChange={handleChange}
                rows="4"
                required
              />
            </div>

            <button type="submit" className="btn-submit-expense">
              ‚úÖ Submit Expense
            </button>
          </form>
        </div>

        <div className="receipt-section">
          <div className="receipt-upload-card">
            <h3>üì∏ Upload Receipt</h3>
            <p className="receipt-subtitle">Upload a receipt to auto-fill expense details</p>

            <div className="upload-area">
              {previewUrl ? (
                <div className="preview-container">
                  <img src={previewUrl} alt="Receipt preview" className="receipt-preview" />
                  <button
                    type="button"
                    className="btn-remove-receipt"
                    onClick={() => {
                      setReceipt(null);
                      setPreviewUrl(null);
                    }}
                  >
                    ‚úï Remove
                  </button>
                </div>
              ) : (
                <label className="upload-label">
                  <input
                    type="file"
                    onChange={handleFileChange}
                    accept="image/*,application/pdf"
                    style={{ display: 'none' }}
                  />
                  <div className="upload-placeholder">
                    <div className="upload-icon">üìÅ</div>
                    <p>Click to upload or drag and drop</p>
                    <small>PNG, JPG or PDF (max. 10MB)</small>
                  </div>
                </label>
              )}
            </div>

            {receipt && (
              <button
                type="button"
                className="btn-ocr"
                onClick={handleOCR}
                disabled={ocrProcessing}
              >
                {ocrProcessing ? 'üîÑ Processing...' : 'ü§ñ Extract Data with OCR'}
              </button>
            )}
          </div>

          <div className="info-card">
            <h4>üí° Tips</h4>
            <ul>
              <li>Upload clear, well-lit receipt images</li>
              <li>OCR will auto-extract amount, date, and merchant</li>
              <li>Review extracted data before submitting</li>
              <li>Include detailed descriptions for faster approval</li>
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ExpenseSubmission;
