import React, { useState, useEffect } from 'react';
import approvalService from '../../services/approvalService';

const PendingApprovals = () => {
  const [workflows, setWorkflows] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedExpense, setSelectedExpense] = useState(null);
  const [comment, setComment] = useState('');
  const [processing, setProcessing] = useState(false);
  const [message, setMessage] = useState('');
  const [error, setError] = useState('');

  useEffect(() => {
    fetchPendingApprovals();
  }, []);

  const fetchPendingApprovals = async () => {
    setLoading(true);
    try {
      const data = await approvalService.getPendingApprovals();
      console.log('Pending approvals:', data);
      setWorkflows(data);
      setError('');
    } catch (error) {
      console.error('Failed to fetch pending approvals:', error);
      setError('Failed to fetch pending approvals');
    } finally {
      setLoading(false);
    }
  };

  const handleApproval = async (expenseId, action) => {
    setProcessing(true);
    setError('');
    setMessage('');

    try {
      console.log('Processing approval:', { expenseId, action, comment });
      await approvalService.processApproval(expenseId, action, comment);
      setMessage(`Expense ${action.toLowerCase()} successfully! ‚úÖ`);
      setComment('');
      setSelectedExpense(null);
      await fetchPendingApprovals();
      setTimeout(() => setMessage(''), 3000);
    } catch (error) {
      console.error('Failed to process approval:', error);
      setError(error.response?.data?.message || 'Failed to process approval');
      setTimeout(() => setError(''), 5000);
    } finally {
      setProcessing(false);
    }
  };

  if (loading) {
    return <div className="loading">Loading...</div>;
  }

  return (
    <div className="pending-approvals">
      <h2>‚è≥ Pending Approvals</h2>
      <p className="subtitle">Review and approve expense requests from your team</p>

      {message && <div className="message">{message}</div>}
      {error && <div className="error">{error}</div>}

      {workflows.length === 0 ? (
        <div className="empty-state">
          <div className="empty-icon">‚úÖ</div>
          <p>No pending approvals</p>
          <p>All expenses have been reviewed!</p>
        </div>
      ) : (
        <div>
          {workflows.map((workflow) => (
            <div key={workflow._id} className="approval-card">
              <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start' }}>
                <div>
                  <h3>üë§ {workflow.expense.employee.name}</h3>
                  <p className="user-email">{workflow.expense.employee.email}</p>
                </div>
                <span className={`status-badge status-${workflow.expense.status.toLowerCase().replace(' ', '-')}`}>
                  {workflow.expense.status}
                </span>
              </div>

              <div style={{ marginTop: '1rem' }}>
                <p>
                  <strong>üí∞ Amount:</strong> ${workflow.expense.amount} {workflow.expense.currency}
                  {workflow.expense.currency !== workflow.expense.company?.currency && (
                    <span> (‚âà ${workflow.expense.amountInCompanyCurrency.toFixed(2)})</span>
                  )}
                </p>
                <p>
                  <strong>üìÇ Category:</strong> {workflow.expense.category}
                </p>
                <p>
                  <strong>üìù Description:</strong> {workflow.expense.description}
                </p>
                <p>
                  <strong>üìÖ Date:</strong> {new Date(workflow.expense.date).toLocaleDateString()}
                </p>
                {workflow.expense.receipt && (
                  <p>
                    <strong>üìé Receipt:</strong> Attached
                  </p>
                )}
              </div>

              <textarea
                placeholder="Add comment (optional)"
                value={selectedExpense === workflow.expense._id ? comment : ''}
                onChange={(e) => {
                  setSelectedExpense(workflow.expense._id);
                  setComment(e.target.value);
                }}
                disabled={processing}
              />

              <div className="approval-actions">
                <button 
                  onClick={() => handleApproval(workflow.expense._id, 'Approved')}
                  disabled={processing}
                >
                  {processing && selectedExpense === workflow.expense._id ? '‚è≥ Processing...' : '‚úÖ Approve'}
                </button>
                <button 
                  onClick={() => handleApproval(workflow.expense._id, 'Rejected')}
                  disabled={processing}
                >
                  {processing && selectedExpense === workflow.expense._id ? '‚è≥ Processing...' : '‚ùå Reject'}
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default PendingApprovals;
