 import React, { useState, useEffect } from 'react';
import approvalService from '../../services/approvalService';
import userService from '../../services/userService';

const ApprovalRuleConfig = () => {
  const [rules, setRules] = useState([]);
  const [users, setUsers] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    ruleType: 'sequential',
    percentageThreshold: 50,
    specificApprover: '',
    amountThreshold: { min: 0, max: 10000 },
    approvers: [],
  });

  useEffect(() => {
    fetchRules();
    fetchUsers();
  }, []);

  const fetchRules = async () => {
    try {
      const data = await approvalService.getApprovalRules();
      setRules(data);
    } catch (error) {
      console.error('Failed to fetch rules');
    }
  };

  const fetchUsers = async () => {
    try {
      const data = await userService.getUsers();
      setUsers(data.filter((u) => u.role === 'Manager' || u.role === 'Admin'));
    } catch (error) {
      console.error('Failed to fetch users');
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await approvalService.createApprovalRule(formData);
      setShowForm(false);
      fetchRules();
    } catch (error) {
      console.error('Failed to create rule');
    }
  };

  return (
    <div className="approval-rules">
      <h2>Approval Rules Configuration</h2>
      <button onClick={() => setShowForm(!showForm)}>
        {showForm ? 'Cancel' : 'Add New Rule'}
      </button>

      {showForm && (
        <form onSubmit={handleSubmit} className="rule-form">
          <input
            type="text"
            placeholder="Rule Name"
            value={formData.name}
            onChange={(e) => setFormData({ ...formData, name: e.target.value })}
            required
          />
          
          <select
            value={formData.ruleType}
            onChange={(e) => setFormData({ ...formData, ruleType: e.target.value })}
          >
            <option value="sequential">Sequential</option>
            <option value="percentage">Percentage</option>
            <option value="specific">Specific Approver</option>
            <option value="hybrid">Hybrid</option>
          </select>

          {(formData.ruleType === 'percentage' || formData.ruleType === 'hybrid') && (
            <input
              type="number"
              placeholder="Percentage Threshold"
              value={formData.percentageThreshold}
              onChange={(e) =>
                setFormData({ ...formData, percentageThreshold: e.target.value })
              }
              min="0"
              max="100"
            />
          )}

          <div className="amount-threshold">
            <input
              type="number"
              placeholder="Min Amount"
              value={formData.amountThreshold.min}
              onChange={(e) =>
                setFormData({
                  ...formData,
                  amountThreshold: { ...formData.amountThreshold, min: e.target.value },
                })
              }
            />
            <input
              type="number"
              placeholder="Max Amount"
              value={formData.amountThreshold.max}
              onChange={(e) =>
                setFormData({
                  ...formData,
                  amountThreshold: { ...formData.amountThreshold, max: e.target.value },
                })
              }
            />
          </div>

          <button type="submit">Create Rule</button>
        </form>
      )}

      <div className="rules-list">
        {rules.map((rule) => (
          <div key={rule._id} className="rule-card">
            <h3>{rule.name}</h3>
            <p>
              <strong>Type:</strong> {rule.ruleType}
            </p>
            <p>
              <strong>Amount Range:</strong> {rule.amountThreshold?.min} -{' '}
              {rule.amountThreshold?.max}
            </p>
            {rule.percentageThreshold && (
              <p>
                <strong>Percentage Threshold:</strong> {rule.percentageThreshold}%
              </p>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default ApprovalRuleConfig;
